import React, { Component } from "react";
import getImg from "../../Utils/getImg";
import "./Home.scss";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }
  componentDidMount() {
    this.setState({
      data: [
        {
          heading: "Lesson 1",
          user: "Jithin Kumar",
          date: "Sun May 02 2021 10:09:06 GMT+0530",
          data:
            "<p>Introduction to music</p><p>All music is made up of 12+1 notes.....</p>",
          files: [],
          comments: [
            {
              user: "sumukh",
              date: "Sun May 02 2021 10:09:06 GMT+0530",
              line: "Nice",
            },
          ],
        },
      ],
    });
  }
  render() {
    return (
      <div className="sa-home-w">
        {this.state.data.map((e, i) => {
          return (
            <div className="eachPost" key={i}>
              <div className="header">
                <div>
                  <div className="headerIcon">{e.user[0].toUpperCase()}</div>
                  <div className="nameAndDate">
                    <div className="namePost">{e.user}</div>
                    <div className="datePost">{e.date}</div>
                  </div>
                </div>
                {this.props.isLoggedIn == "admin" ? (
                  <div className="editPost" onClick={() => null}>
                    Edit
                  </div>
                ) : (
                  <div className="editPost"></div>
                )}
              </div>
              <div className="body">
                <div className="mainBody">
                  <div className="postHead">{e.heading}</div>
                  <div
                    className="postBody"
                    dangerouslySetInnerHTML={{ __html: e.data }}
                  />
                  <div className="attachedFiles">
                    {e.files &&
                      e.files.map((f, ind) => {
                        return (
                          <img
                            className="eachFile"
                            src={f}
                            alt={f.name}
                            key={ind}
                          />
                        );
                      })}
                  </div>
                </div>
                <div className="comments">
                  <div className="commentsHeader">Comments</div>
                  <div className="commentsBody">
                    {e.comments &&
                      e.comments.map((c, ind) => {
                        return (
                          <div className="eachComment" key={ind}>
                            <div className="commentUser">{c.user}</div>
                            <div className="commentDate">{c.date}</div>
                            <div className="commentLine">{c.line}</div>
                          </div>
                        );
                      })}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Home;
