import React from "react";

// import SfLogo from "../../assets/images/sa-logo.png";
// import SPLogo from "../../assets/images/squarepanda-logo.png";
// import UsersLink from "../../assets/images/users-link.png";
// import ContentLink from "../../assets/images/content-link.png";
// import QuizLink from "../../assets/images/quiz-link.png";

import "./SideNav.css";

const SideNav = (props) => {
  const [selectedMenuItem, setSelectedMenuItem] = React.useState("users");

  React.useEffect(() => {
    if (props.route === "/users") {
      setSelectedMenuItem("users");
    } else if (props.route === "/content") {
      setSelectedMenuItem("content");
    } else if (props.route === "/quiz") {
      setSelectedMenuItem("quiz");
    }
  }, [props.route]);
  return (
    <div className="sa-sidenav-w">
      <div className="logo center">A Strum Away</div>
      <div className="greeting center">
        <div>Square Panda</div>
      </div>
      <div className="sa-menu">
        <div
          className={`sa-menu-link ${
            selectedMenuItem === "users" ? "selected" : ""
          }`}
          onClick={() => props.goto("/users")}
        >
          {/* <img src={UsersLink} alt="Users Link" /> */}
          <div>Users</div>
        </div>
        <div
          className={`sa-menu-link ${
            selectedMenuItem === "content" ? "selected" : ""
          }`}
          onClick={() => props.goto("/content")}
        >
          {/* <img
            src={ContentLink}
            alt="Content Link"
            style={{ marginRight: "12px" }}
          /> */}
          <div>Content</div>
        </div>
        <div
          className={`sa-menu-link ${
            selectedMenuItem === "quiz" ? "selected" : ""
          }`}
          onClick={() => props.goto("/quiz")}
        >
          {/* <img src={QuizLink} alt="Quiz Link" /> */}
          <div>Quiz</div>
        </div>
      </div>
    </div>
  );
};

export default SideNav;
