import React from "react";
import getImg from "../../Utils/getImg";
import "./Home.scss";

const AddPost = (props) => {
  const [file, setfile] = React.useState([]);
  return (
    <div className="sa-modal-w">
      <div className="sa-modal-box">
        <img
          src={getImg("cross.svg")}
          style={{ position: "relative", right: "-50%" }}
          className="close-btn"
        />

        <div className="headingEntry">
          Heading
          <input id="postHead" />
        </div>
        <textarea id="postData" placeholder="Add Data" />
        <p>Attach Files</p>
        <div className="postFiles">
          {file &&
            file.map((f, ind) => {
              return (
                <img className="eachFile" src={f} alt={f.name} key={ind} />
              );
            })}
          <div className="eachFile">
            <img id="eachFileAdd" src={getImg("cross.svg")} alt={"addFile"} />
          </div>
        </div>
        <button name="Add Post" className="sa-login-btn">
          Add Post
        </button>
      </div>
    </div>
  );
};

export default AddPost;
