import React from "react";
import getImg from "../../Utils/getImg";
import "./Home.scss";

const AddPost = (props) => {
  return (
    <div className="sa-modal-w">
      <div className="sa-modal-box">
        <div className="headingEntry">
          Heading
          <input id="postHead" />
        </div>
        <textarea id="postData" />
        <div className="postFiles">
          {file &&
            file.map((f, ind) => {
              return (
                <img className="eachFile" src={f} alt={f.name} key={ind} />
              );
            })}
          <img className="eachFile" src={getImg("cross.svg")} alt={"addFile"} />
        </div>
      </div>
    </div>
  );
};

export default AddPost;
