import React from "react";
import getImg from "./../../Utils/getImg";
import "./Login.css";

const Login = (props) => {
  React.useEffect(() => {
    if (props.isLoggedIn) {
      props.goto("/users");
    }
  }, [props]);

  const [terms, setTerms] = React.useState(false);

  function acceptTerms() {
    setTerms(!terms);
  }

  function showPass() {
    let pass = document.getElementById("password");
    if (pass.type === "text") {
      pass.type = "password";
    } else {
      pass.type = "text";
    }
  }

  function login() {
    sessionStorage.setItem("loggedIn", "true");
    props.goto("/users");
  }

  return !props.isLoggedIn ? (
    <div className="sf-login-w">
      <div className="login-container">
        <div className="left-container ">
          <div>
            <img
              alt="sensefroth logo"
              // src={getImg("SF-Logo1.png")}
              style={{ width: "100px" }}
            />
          </div>
          <div className="form-container">
            <div className="welcome-text" style={{ whiteSpace: "nowrap" }}>
              <span>Hello</span>
              <br />
              <span>Welcome to Admin Portal.</span>
            </div>
            <div style={{ padding: "1.25rem 1.25rem 0px" }}>
              <input
                type="email"
                id="username"
                className="form-control login-input"
                placeholder="Email"
              />

              <div style={{ position: "relative" }}>
                <input
                  type="password"
                  id="password"
                  className="login-input"
                  placeholder="Password"
                />
                <span
                  style={{ position: "absolute", left: "97%", top: "15%" }}
                  className="clickable"
                  onClick={() => showPass()}
                >
                  {/* <img src={getImg("show-eye.svg")} alt="show password" /> */}
                </span>
              </div>

              <div className="privacy-license-container">
                <div className="privacy-container">
                  <span style={{ marginRight: "5px", width: "20px" }}>
                    <img
                      style={{ width: "100%" }}
                      // src={getImg(
                      //   terms ? "checkboxChecked.svg" : "checkboxUnchecked.svg"
                      // )}
                      onClick={() => acceptTerms()}
                      alt="checkbox"
                    />
                  </span>
                  <span className="mandatory-field">* </span>
                  <span>
                    I have read and accepted the
                    <span className="privacy-url"> Privacy Policy</span>
                  </span>
                </div>
              </div>
              <div>
                <input
                  type="submit"
                  className={`btn btn-primary btn-block sf-login-btn  ${
                    !terms ? "disabled" : ""
                  }`}
                  value="Sign In"
                  onClick={() => login()}
                />
              </div>

              <div className="powered-by">
                <div className="branding hoverable">
                  <img
                    // src={getImg("sf-logo.png")}
                    style={{ width: "105px" }}
                    alt="sf logo"
                  />
                  &nbsp;| Powering the Conversational Enterprise
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="right-container">
          <img
            // src={getImg("index.png")}
            style={{ width: "90%" }}
            alt="login banner"
          />
        </div>
      </div>
    </div>
  ) : null;
};

export default Login;
