import React from "react";
import Toaster from "../../ReusableComponents/Toaster";
import DefaultLayout from "./../../Components/DefaultLayout";
import { Router, useHistory } from "react-router-dom";
import { BrowserRouter as Route, Switch } from "react-router-dom";
import Home from "./../../Components/Home/index";

import "./Content.css";

const Content = (props) => {
  const history = useHistory();
  const [toaster, setToaster] = React.useState({
    showToaster: false,
    toasterMessage: "",
  });

  // const [isLoaderVisible, setIsLoaderVisible] = React.useState(false);

  React.useEffect(() => {
    if (!props.isLoggedIn) {
      history.push("/");
    }
  }, [props]);

  const renderToaster = (toasterMessage) => {
    setToaster({ showToaster: true, toasterMessage: toasterMessage });
    setTimeout(() => {
      setToaster({
        showToaster: false,
        toasterMessage: "",
      });
    }, 3000);
  };

  return props.isLoggedIn ? (
    <DefaultLayout
      isLoggedIn={props.userType}
      userName={props.userName}
      setUserType={props.setUserType.bind(this)}
    >
      <div className="sa-content-w">
        <Switch>
          <Route path="/users/profile">B</Route>
          <Route path="/users">
            <Home />
          </Route>
        </Switch>
      </div>
      {toaster.showToaster ? (
        <Toaster
          toastList={{
            description: toaster.toasterMessage,
            backgroundColor: "#000",
          }}
          position="top-center"
          deleteTime={3000}
        />
      ) : null}
    </DefaultLayout>
  ) : null;
};

export default Content;
