import React from "react";

// import SPLogo from "../../assets/images/squarepanda-logo.png";
// import HeaderProfileDropdown from "../../assets/images/header-profile-dropdown.png";

import "./Header.scss";

const HEADER_TABS_MAPPING = {
  "/users": ["MANAGE USERS", "GROUPS"],
  "/content": ["COURSE", "TOPICS"],
  "/quiz": ["MANAGE QUIZ", "SCHEDULE QUIZ"],
};

const Header = (props) => {
  const [headerTabs, setHeaderTabs] = React.useState([]);
  const [isPopupOpen, setIsPopupOpen] = React.useState(false);
  let logOutRef = React.useRef();
  React.useEffect(() => {
    let handler = (event) => {
      if (isPopupOpen) {
        var logout = logOutRef.current.contains(event.target);
        if (logout == false) {
          setIsPopupOpen(false);
        }
      }
    };
    document.addEventListener("mousedown", handler);
    return () => {
      document.removeEventListener("mousedown", handler);
    };
  }, [isPopupOpen]);

  React.useEffect(() => {
    if (HEADER_TABS_MAPPING[props.route]) {
      setHeaderTabs(HEADER_TABS_MAPPING[props.route]);
    }
  }, [props.route]);

  function logout() {
    sessionStorage.setItem("loggedIn", "false");
    props.goto("/");
  }

  return (
    <>
      <div className="sf-header-w">
        <div className="sf-header-tabs-w">
          {headerTabs.map((h, i) => {
            return (
              <div
                key={i}
                className={`sf-header-tab center ${
                  props.selectedHeaderTab === i ? "selected" : ""
                }`}
                onClick={() => props.updateSelectedHeaderTab(i)}
              >
                {h}
              </div>
            );
          })}
        </div>
        <div className="sf-header-profile-section">
          <div className="left">
            <div className="name">John Doe</div>
            <div className="logo">
              {/* <img src={SPLogo} alt="Squarepanda logo" /> */}
            </div>
          </div>
          <div className="right">
            <div className="icon">
              {/* <img
                src={HeaderProfileDropdown}
                alt="Header Profile Dropdown"
                onClick={() => {
                  setIsPopupOpen(!isPopupOpen);
                }}
              /> */}
            </div>
          </div>
        </div>
      </div>
      {isPopupOpen ? (
        <div className="sf-header-popup" ref={logOutRef}>
          <div className="sf-header-popup-option" onClick={() => logout()}>
            Logout
          </div>
        </div>
      ) : (
        <></>
      )}
    </>
  );
};

export default Header;
