import React, { Component } from "react";
import getImg from "../../Utils/getImg";
import "./Home.scss";
import f1 from "./../../assets/images/guitarBack.jpg";
import AddPost from "./AddPost";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      addModal: false,
      template: { heading: "", data: "", files: [] },
    };
  }
  componentDidMount() {
    this.setState({
      data: [
        {
          id: "p0001",
          heading: "Lesson 1",
          user: "Jithin Kumar",
          date: "Sun May 02 2021 10:09:06 GMT+0530",
          data:
            "<p>Introduction to music</p><p>All music is made up of 12+1 notes.....</p>",
          files: [f1],
          comments: [
            {
              user: "sumukh",
              date: "Sun May 02 2021 10:09:06 GMT+0530",
              line: "Nice",
            },
          ],
        },
      ],
    });
  }

  toggleAddModal = (val) => {
    this.setState({
      addModal: val,
    });
  };

  toggleEditModal = (id) => {
    let x = this.state.template;
    let f = this.state.data.map((e) => {
      if (e.id == id) return e;
    });
    x["heading"] = f[heading];
    x["data"] = f.data;
    x["files"].push(f.files);
    this.setState({
      template: x,
    });
  };

  render() {
    return (
      <div className="sa-home-w">
        {this.state.data.map((e, i) => {
          return (
            <div className="eachPost" key={i}>
              <div className="header">
                <div>
                  <div className="headerIcon">{e.user[0].toUpperCase()}</div>
                  <div className="nameAndDate">
                    <div className="namePost">{e.user}</div>
                    <div className="datePost">{e.date.split("GMT")[0]}</div>
                  </div>
                </div>
                {this.props.isLoggedIn == "admin" ? (
                  <div
                    className="editPost"
                    onClick={() => this.toggleEditModal(e.id)}
                  >
                    Edit
                  </div>
                ) : (
                  <div className="editPost"></div>
                )}
              </div>
              <div className="body">
                <div className="mainBody">
                  <div className="postHead">{e.heading}</div>
                  <div
                    className="postBody"
                    dangerouslySetInnerHTML={{ __html: e.data }}
                  />
                  <div className="attachedFiles">
                    {e.files &&
                      e.files.map((f, ind) => {
                        return (
                          <img
                            className="eachFile"
                            src={f}
                            alt={f.name}
                            key={ind}
                          />
                        );
                      })}
                  </div>
                </div>
                <div className="comments">
                  <div className="commentsHeader">Comments</div>
                  <div className="commentsBody">
                    {e.comments &&
                      e.comments.map((c, ind) => {
                        return (
                          <div className="eachComment" key={ind}>
                            <div className="commentUser">{c.user}</div>
                            <div className="commentDate">
                              {c.date.split("GMT")[0]}
                            </div>
                            <div className="commentLine">{c.line}</div>
                          </div>
                        );
                      })}
                  </div>
                  <input
                    type="text"
                    className="commentsInput"
                    placeholder="Write a comment"
                  />
                </div>
              </div>
            </div>
          );
        })}

        {this.props.isLoggedIn == "admin" ? (
          <span class="sa-add-post">
            <img
              src={getImg("cross.svg")}
              alt="Add Post"
              onClick={() => this.toggleAddModal(true)}
            />
          </span>
        ) : null}
        {this.state.addModal ? (
          <AddPost toggleAddModal={this.toggleAddModal.bind(this)} />
        ) : null}
      </div>
    );
  }
}

export default Home;
