import React from "react";
import getImg from "./../../Utils/getImg";
import "./Login.css";

const Login = (props) => {
  React.useEffect(() => {
    if (props.isLoggedIn) {
      props.goto("/users");
    }
  }, [props]);

  const [terms, setTerms] = React.useState(false);

  function acceptTerms() {
    setTerms(!terms);
  }

  function showPass() {
    let pass = document.getElementById("password");
    if (pass.type === "text") {
      pass.type = "password";
    } else {
      pass.type = "text";
    }
  }

  function login() {
    sessionStorage.setItem("loggedIn", "true");
    props.goto("/users");
  }

  return !props.isLoggedIn ? (
    <div className="sf-login-w">
      <div className="login-container">
        <div className="left-container ">
          <div className="form-container">
            <div className="welcome-text" style={{ whiteSpace: "nowrap" }}>
              <span>Welcome to A Strum Away</span>
            </div>
            <div style={{ padding: "1.25rem 1.25rem 0px" }}>
              <input
                type="email"
                id="username"
                className="form-control login-input"
                placeholder="Email"
              />

              <div style={{ position: "relative" }}>
                <input
                  type="password"
                  id="password"
                  className="login-input"
                  placeholder="Password"
                />
                <span
                  style={{ position: "absolute", left: "97%", top: "15%" }}
                  className="clickable"
                  onClick={() => showPass()}
                >
                  <img src={getImg("show-eye.svg")} alt="show password" />
                </span>
              </div>
              <div>
                <input
                  type="submit"
                  className={`btn btn-primary btn-block sf-login-btn  ${
                    !terms ? "disabled" : ""
                  }`}
                  value="Sign In"
                  onClick={() => login()}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  ) : null;
};

export default Login;
