import React from "react";
import Toaster from "../../ReusableComponents/Toaster";
import DefaultLayout from "./../../Components/DefaultLayout";

import "./Content.css";

const Content = (props) => {
  const [toaster, setToaster] = React.useState({
    showToaster: false,
    toasterMessage: "",
  });
  const [isLoaderVisible, setIsLoaderVisible] = React.useState(false);

  React.useEffect(() => {
    if (!props.isLoggedIn) {
      props.goto("/");
    }
  }, [props]);

  const renderToaster = (toasterMessage) => {
    setToaster({ showToaster: true, toasterMessage: toasterMessage });
    setTimeout(() => {
      setToaster({
        showToaster: false,
        toasterMessage: "",
      });
    }, 3000);
  };

  return props.isLoggedIn ? (
    <DefaultLayout
      goto={props.goto}
      route={props.route}
      selectedHeaderTab={props.selectedHeaderTab}
      updateSelectedHeaderTab={props.updateSelectedHeaderTab}
    >
      <div className="sf-content-w">
        {props.selectedHeaderTab === 0
          ? // <CourseSection
            //   goto={props.goto}
            //   route={props.route}
            //   isLoaderVisible={isLoaderVisible}
            //   setIsLoaderVisible={setIsLoaderVisible}
            // />
            null
          : // <TopicSection
            //   goto={props.goto}
            //   route={props.route}
            //   // quizData={quizTableData}
            //   // fetchQuizData={fetchQuizData}
            //   isLoaderVisible={isLoaderVisible}
            //   // handleFilterChange={filterQuizData}
            //   setIsLoaderVisible={setIsLoaderVisible}
            // />
            null}
      </div>
      {toaster.showToaster ? (
        <Toaster
          toastList={{
            description: toaster.toasterMessage,
            backgroundColor: "#000",
          }}
          position="top-center"
          deleteTime={3000}
        />
      ) : null}
    </DefaultLayout>
  ) : null;
};

export default Content;
