import React, { Component } from "react";
import getImg from "../../Utils/getImg";
import "./Home.scss";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [
        {
          heading: "Lesson 1",
          user: "Jithin Kumar",
          date: "Sun May 02 2021 10:09:06 GMT+0530",
          data:
            "<p>Introduction to music</p><p>All music is made up of 12+1 notes.....</p>",
          files: [],
          comments: [
            {
              user: "sumukh",
              date: "Sun May 02 2021 10:09:06 GMT+0530",
              line: "Nice",
            },
          ],
        },
      ],
    };
  }

  render() {
    return (
      <div className="sa-home-w">
        {this.state.data.map((e, i) => {
          return (
            <div className="eachPost">
              <div className="header">
                <div>
                  <div className="headerIcon">{e.heading[0].toUpperCase()}</div>
                  <div className="nameAndDate">
                    <div className="namePost">{e.heading}</div>
                    <div className="datePost">{e.date}</div>
                  </div>
                </div>
                {this.props.isLoggedIn == "admin" ? (
                  <div className="editPost">Edit</div>
                ) : null}
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Home;
