import React, { Component } from "react";
import getImg from "../../Utils/getImg";
import "./Schedule.scss";

const colorChart = {
  0: "#f26157",
  1: "#65b268",
  2: "#8590c9",
  3: "#f4e45b",
};

class Schedule extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeTable: [
        { day: "Monday", slots: [null, null, null] },
        { day: "Tuesday", slots: [null, null, null] },
        { day: "Wednesday", slots: [null, null, null] },
        { day: "Thursday", slots: [null, null, null] },
        { day: "Friday", slots: [null, null, null] },
      ],
    };
  }

  setSlot(day, slot) {
    let x = this.state.timeTable;
    x[day].slots[slot] =
      x[day].slots[slot] == null ? 0 : (x[day].slots[slot] + 1) % 4;
    this.setState({
      timeTable: x,
    });
  }

  render() {
    return (
      <div className="sa-schedule-w">
        {this.props.isLoggedIn == "admin" ? (
          <div className="student-list"></div>
        ) : null}
        <div className="timeTable">
          <table>
            <tr>
              <th>Days/Slots</th>
              <th>
                Slot 1<br />
                6AM-7AM
              </th>
              <th>
                Slot 2<br />
                3PM-4PM
              </th>
              <th>
                Slot 3<br />
                7PM-8PM
              </th>
            </tr>

            {this.state.timeTable.map((day, ind) => {
              return (
                <tr>
                  <th>{day.day}</th>
                  {day.slots.map((slot, i) => (
                    <td onClick={() => this.setSlot(ind, i)}>{slot}</td>
                  ))}
                </tr>
              );
            })}
          </table>
        </div>
      </div>
    );
  }
}

export default Schedule;
